import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-team',
  templateUrl: './team.component.html',
  styleUrls: ['./team.component.css'],
})
export class TeamComponent {
  @Input() team: string[] = [];
  @Input() index = 0;
}
<div class="team-container">
      <h3>Team  {{ index + 1 }}</h3>
      <ul>
        <li *ngFor="let member of team">{{ member | titlecase }}</li>
      </ul>
    </div>
